import React, {useEffect, useState} from 'react';
import {StyleSheet, View, Text, ImageBackground, Button, TextInput, TouchableOpacity} from 'react-native';
import  AsyncStorage  from '@react-native-community/async-storage';
// import StyledButton from '../StyledButton';
// import styles from './style';
import axios from 'axios';
import * as SecureStore from 'expo-secure-store';

const Login = (props) => {
// const {name,tagline,image} = props;
const [savedUserData, setSavedUserData] = useState({ email: '', password: ''});
const [isloggedin, setLogged] = useState(false)

const clickHandler = async (req) => {

   axios.post('http://192.168.1.130:5000/signin', savedUserData) //connected to the server port
   .then( req=> {
     const token = req.data.token
     AsyncStorage.setItem('token', token)
     const isloggedin =  AsyncStorage.getItem('token')
    // let checkToken = AsyncStorage.getItem('token')
    // console.log(checkToken)
    //  SecureStore.setItemAsync('secure_token', req.data.token);
    // const token =  SecureStore.getItemAsync('secure_token');
    //  console.log(token)
         if(isloggedin) {
                setLogged(true)
            }else{
              setLogged(false)
             
            }
    console.log(req.data.token)
    // setSavedUserData(savedUserData)
    console.log(savedUserData)
    console.log("success")
   })
   .catch((error) => {
       console.log(error)
       console.log("error")
   })
   console.log(isloggedin)
}

//   useEffect(() => {
//     const token =  AsyncStorage.getItem('token')
    
//     if(token) {
//         setLogged(true)
//     }else{
//       setLogged(false)
     
//     }
//     console.log(isloggedin)
//   },[])

  const logout = async () => {
    console.log(isloggedin) 
    await AsyncStorage.removeItem('token')
    console.log(isloggedin)
  }
    return (
        <View >
            {/* <Text>{value}</Text> */}
            <Text style = {styles.margin}>Log in</Text>
            <View style = {styles.buttonContainer}>
            {/* <Text>My name is {name}</Text> */}
            <Text>Enter your Email</Text>
            <TextInput 
            style={styles.input}
            placeholder='example@xxxmail.com'
            // onChangeText={(val) => setSavedUserData(val)}
            onChangeText={(email) => setSavedUserData({...savedUserData, email: email})}/>

            <Text>Enter your Password</Text>
            <TextInput 
            // keyboardType='default'
            style={styles.input}
            placeholder='e.g .!@#123Aa'
            onChangeText={(password) => setSavedUserData({...savedUserData, password:password})}/>
            <Button title = "login" onPress = {clickHandler}/>
            <Button title = "logout" onPress = {logout}/>
                </View>
            </View>
                )
}

const styles = StyleSheet.create({
containers: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
buttonContainer: {
    marginTop: 20
},
margin: {
    margin: 30
},
input: {
    borderWidth: 1,
    borderColor: '#777',
    padding: 8,
    margin:10,
    width: 200,
}
})

export default Login;
