    console.log(req.data.token)
    // setSavedUserData(savedUserData)
    console.log(savedUserData)
    console.log("success")
   })
   .catch((error) => {
       console.log(error)
       console.log("error")
   })

   const saveValueFunction = () => {
    //function to save the value in AsyncStorage
    if (textInputValue) {
      //To check the input not empty
      AsyncStorage.setItem('token', token);
      //Setting a data to a AsyncStorage with respect to a key
      setTextInputValue('');
      //Resetting the TextInput
      console.log('Data Saved');
      //alert to confirm
    } else {
      console.log('Please fill data');
      //alert for the empty InputText
    }
  };

  const getValueFunction = () => {
    //function to get the value from AsyncStorage
    AsyncStorage.getItem('token').then(
      (token) =>
        //AsyncStorage returns a promise so adding a callback to get the value
        setGetValue(token)
      //Setting the value in Text
    );
  };

//    const isloggedin =  AsyncStorage.getItem('token')
//    if(isloggedin.length > 0) {
//        console.log('loggedin')
//     // setLogged(true)
// }else{
//     console.log('not loggedin')
// //   setLogged(false)
 
// }
// // console.log(isloggedin)
}

//   useEffect(() => {
//     const token =  AsyncStorage.getItem('token')
    
//     if(token) {
//         setLogged(true)
//     }else{
//       setLogged(false)
     
//     }
//     console.log(isloggedin)
//   },[])

  const logout = async () => {
       await AsyncStorage.removeItem('token')
    const isloggedin =  AsyncStorage.getItem('token')
   
    if(isloggedin.length > 0) {
        console.log('loggedin')
     // setLogged(true)
 }else{
     console.log('not loggedin')
 //   setLogged(false)
  
 }

  }
    return (
        <View >
            {/* <Text>{value}</Text> */}
            <Text style = {styles.margin}>Log in</Text>
            <View style = {styles.buttonContainer}>
            {/* <Text>My name is {name}</Text> */}
            <Text>Enter your Email</Text>
            <TextInput 
            style={styles.input}
            placeholder='example@xxxmail.com'
            // onChangeText={(val) => setSavedUserData(val)}
            onChangeText={(email) => setSavedUserData({...savedUserData, email: email})}/>

            <Text>Enter your Password</Text>
            <TextInput 
            // keyboardType='default'
            style={styles.input}
            placeholder='e.g .!@#123Aa'
            onChangeText={(password) => setSavedUserData({...savedUserData, password:password})}/>
            <Button title = "login" onPress = {clickHandler}/>
            <Button title = "logout" onPress = {logout}/>
                </View>
            </View>
                )
}

const styles = StyleSheet.create({
containers: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
buttonContainer: {
    marginTop: 20
},
margin: {
    margin: 30
},
input: {
    borderWidth: 1,
    borderColor: '#777',
    padding: 8,
    margin:10,
    width: 200,
}
})

export default Login;
