import React, { useEffect ,useState } from 'react';
import {View, Text, ImageBackground,TextInput,Button} from 'react-native';
import { storage } from './firbase'
import ImagePicker from 'react-native-image-picker'
import axios from 'axios';

const EditItems = async (props) => {
   var ID = props.route.params.id
   console.log(ID)
   
   useEffect(  () => {
    axios.get('http://127.0.0.1:5000/ItemsList')   
  .then( res => {
      // console.log (res.data)
      setOrders(res.data)
  })
  .catch((error) => {
      console.log(error);
  })
 
}, []);

const [orders, setOrders] = useState('')
const Filter =  Object.entries(orders).filter(([key,v]) => v.itemID==ID) 
console.log(orders)
console.log(Filter)


    
   const [image, setImage] = useState(null)
  



   const onChooseImagePress = async () => {
     let result = await ImagePicker.launchImageLibraryAsync({
         mediaTypes: ImagePicker.MediaTypeOptions.All,
         allowsEditing: true,
         aspect: [4, 3],
         quality: 1,
       });
       console.log(result)
     if (!result.cancelled){
       var json = JSON.stringify(result.height)
       setImage(result.uri)
         uploadImage(result.uri , json)
         .then (()=>{
   
    console.log ('success');
         })
         .catch((error)=>{
             console.log (error);
         });
   
     }
   }
         const uploadImage = async (uri,imageName)=>{
         const response = await fetch(uri);
         const blob = await response.blob();
         const  ref = storage.ref().child('images/' + imageName).put(blob);
         const saved =storage.ref("images").child(imageName).getDownloadURL().then(url => { 
           
           orderData.image = url 
           console.log(url)
                      })
                   
          }
        
    //       const [orderData, setOrderData] = useState({  category: '', quantity: '',
    //         weight:'', description:'', price:''
    //    ,image:'', location:localStorage.getItem('location'),
    //    status:"Pending", user_id:localStorage.getItem('user_id')})
    //   console.log(orderData)
      
    

      const [orderData, setOrderData] = useState({  category: Filter[0][1].category, quantity: Filter[0][1].quantity,
        weight:Filter[0][1].weight, description: Filter[0][1].description, price:Filter[0][1].price
   ,image:Filter[0][1].image, location:localStorage.getItem('location'),
   status:"Pending", user_id:localStorage.getItem('user_id')})
  console.log(orderData)




const Edit = () =>{
    axios.put('http://127.0.0.1:5000/items/'+ID,orderData).then(req => {
       console.log(req)
    })
    .catch((error) => {
        console.log(error);
    })
    console.log(orderData)
    
}


    return (
        <View >

        <View style={{ flex: 1, alignItems: 'center', justifyContent: 'center' }}>
      {orderData.image && <Image source={{ uri: orderData.image }} style={{ width: 200, height: 200 }} />}
      <Button title="Pick an image from camera roll" onPress={onChooseImagePress} />
            </View>

            <View>
            <Text>category:</Text>
            <TextInput
            style={{height: 40}}
            placeholder="Type here your category!"
            onChangeText={text => setOrderData({...orderData ,category:text})}
            defaultValue={orderData.category}
             />
             <Text>quantity:</Text>
            <TextInput
            style={{height: 40}}
            placeholder="Type here the quantity!"
            onChangeText={text => setOrderData({...orderData ,quantity:text})}
            defaultValue={orderData.quantity}
             />
             <Text>weight:</Text>
            <TextInput
            style={{height: 40}}
            placeholder="Type here the weight!"
            onChangeText={text => setOrderData({...orderData ,weight:text})}
            defaultValue={orderData.weight}
             />
             <Text>description:</Text>
            <TextInput
            style={{height: 40}}
            placeholder="Type here the description!"
            onChangeText={text => setOrderData({...orderData ,description:text})}
            defaultValue={orderData.description}
             />
             <Text>price:</Text>
            <TextInput
            style={{height: 40}}
            placeholder="Type here the price!"
            onChangeText={text => setOrderData({...orderData ,price:text})}
            defaultValue={orderData.price}
             />
              <Text>image:</Text>
              <TextInput
            style={{height: 40}}
            placeholder="enter url"
            onChangeText={text => setOrderData({...orderData ,image:text})}
            defaultValue={orderData.image}
             />
          <Button
        title="Edit features"
        onPress={Edit}
      />
            </View>

        </View>
    )
}

export default EditItems;
