import React, {useEffect, useState}from 'react';
import {View, Text, ImageBackground, Image, StyleSheet} from 'react-native';
// import StyledButton from '../StyledButton';
// import styles from './style';
import axios from 'axios';

const Profile = () => {
// const {name,tagline,image} = props;

//to know which user's profile
var User=localStorage.getItem('user_id');

//hooks
const [profile, setProfile] = useState();
console.log(profile);

useEffect( () => {
    axios.get('http://localhost:5000/UserProfile')
    .then(res => {
        setProfile(res.data);
        // console.log(res.data);
    })
    .catch((error) => {
        console.log(error);
    })
},[])

    return (
        <View>
        {Object.entries(profile).map(([key,v])=>{
            return <View key={key}>
                   <Text>
                  <Image
        	 style={styles.tinyLogo}
             source={{
                 uri: v.image,
                 }}
                    /> 
             {/* <Text>category : {v.category}</Text>
            <Text> quantity: {v.quantity}</Text>
            <Text> weight: {v.weight}</Text>
            <Text> description: {v.description}</Text> */}
            </Text>
            </View>
        })}
        </View>
    )
}

export default Profile;
