import axios from 'axios';

const Login = (props) => {
// const {name,tagline,image} = props;
const [savedUserData, setSavedUserData] = useState({ email: '', password: ''});
console.log(email)
// const [name, setName] = useState('Sahar')
// const clickHandler = () => {
//     setName('Al Issa')
//     console.log("pressed")
// }

const clickHandler = (e) => {
   axios.post('http://localhost:5000/signin')
   .then( res => {
    setSavedUserData(res.data)
    console.log(res.data)
   })
   .catch((error) => {
       console.log(error)
   })
}

    return (
        <View >

            <Text style = {styles.margin}>Log in</Text>
            <View style = {styles.buttonContainer}>
            {/* <Text>My name is {name}</Text> */}
            <Text>Enter your Email</Text>
            <TextInput 
            style={styles.input}
            placeholder='example@xxxmail.com'
            // onChangeText={(val) => setSavedUserData(val)}
            onChangeText={(email) => setSavedUserData({email: savedUserData.email})}/>

            <Text>Enter your Password</Text>
            <TextInput 
            // keyboardType='default'
            style={styles.input}
            placeholder='e.g .!@#123Aa'
            onChangeText={(password) => setSavedUserData({password: savedUserData.password})}/>
            <Button title = "login" onPress = {clickHandler}/>
                </View>
            </View>
                )
}

const styles = StyleSheet.create({
containers: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
buttonContainer: {
    marginTop: 20
},
margin: {
    margin: 30
},
input: {
    borderWidth: 1,
    borderColor: '#777',
    padding: 8,
    margin:10,
    width: 200,
}
})

export default Login;
