import React, {useEffect, useState}from 'react';
import {View, Text, Image, StyleSheet} from 'react-native';
// import StyledButton from '../StyledButton';
// import styles from './style';
import axios from 'axios';

const Profile = () => {
// const {name,tagline,image} = props;

//to know which user's profile
// var User=localStorage.getItem('user_id');

//hooks to set state
const [profile, setProfile] = useState("");
// console.log(profile);

//to get the data directly
useEffect( () => {
    axios.get('http://localhost:5000/UserProfile')
    .then(res => {
        setProfile(res.data);
        console.log(res.data);
    })
    .catch((error) => {
        console.log(error);
    })
},[]);


// const styles = StyleSheet.create({
//     container: {
//       paddingTop: 50,
//     },
//     tinyLogo: {
//       width: 150,
//       height: 150,
//     },
//     logo: {
//       width: 66,
//       height: 58,
//     },
//   });


return (
    <View>
{Object.entries(profile).filter(([key, v]) => 
    v.userID ===1).map(([key,v]) => {
        return  <View key={key}>
        <Text>

        <Image  source={{ uri: v.image,}}/><br/>
        <Text>username: {v.username}</Text><br/>
        <Text>email: {v.email}</Text><br/>
        <Text>phone number: {v.phoneNumber}</Text><br/>

        </Text>

   </View>

    })}

     </View>
)

}

export default Profile;

